/**
 * Title jumbotron
 * @class ApJumbotronTitle
 */

'use strict'

import React, {Component, PropTypes as types} from 'react'
import classnames from 'classnames'
import {shallowEqual} from 'asobj'

/** @lends ApJumbotronTitle */
class ApJumbotronTitle extends Component {
  render () {
    const s = this
    let { props } = s

    return (
      <h1 className={ classnames('ap-jumbotron-title', props.className) }
          style={ Object.assign({}, props.style) }>
        { props.children }
      </h1>
    )
  }

  shouldComponentUpdate (nextProps, nextState) {
    const s = this
    let { props, state } = s
    return !shallowEqual(props, nextProps) || !shallowEqual(state, nextState)
  }
}

Object.assign(ApJumbotronTitle, {

  // --------------------
  // Specs
  // --------------------

  propTypes: {},

  defaultProps: {}
})

export default ApJumbotronTitle
