/**
 * Test case for apLayoutMixin.
 * Runs with mocha.
 */
'use strict'

import ApLayoutMixin from '../lib/ap_layout_mixin'
import React from 'react'
import ReactDOM from 'react-dom/server'

import assert from 'assert'
import co from 'co'

describe('ap-layout-mixin', function () {
  this.timeout(3000)

  before(() => {

  })

  after(() => {

  })

  it('Demo mixin', () => {
    const MockClass = React.createClass({
      mixins: [
        ApLayoutMixin
      ],
      render () {
        const s = this
        return React.createElement('div', {})
      },
      getInitialLayouts () {
        return { height: 100 }
      },
      calcLayouts () {
        return null
      },
      componentWillMount () {
        const s = this
        assert.ok(s.$apLayoutMixin, true)
        assert.deepEqual(s.layouts, { height: 100 })
      }
    })
    let elm = (<MockClass />)
    assert.ok(elm)
  })
})

