/**
 * Test case for index.
 * Runs with mocha.
 */
'use strict'

const index = require('../lib/index.js')
const assert = require('assert')
const co = require('co')

describe('index', () => {
  before(() => co(function * () {
  }))

  after(() => co(function * () {
  }))

  it('index', () => co(function * () {
    for (let name of Object.keys(index)) {
      assert.ok(index[ name ], `${name} should be defined`)
    }
  }))
})

